import React from 'react';
import '../assets/styles/CartPage.css';
import { useNavigate } from 'react-router-dom';

const CartPage = ({ cart, handleQuantityChange, removeFromCart }) => {
  const navigate = useNavigate();

  const handleCheckout = () => {
    // Navigate to the checkout page or display a message
    // In this case, we're just going to log and clear the cart for demonstration
    console.log('Proceeding to checkout...');
    // Optionally, clear cart after checkout
    // removeFromCart();
    navigate('/checkout');
  };

  return (
    <div className="cart-page">
      <h1>Your Cart</h1>
      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <div className="cart-items">
          {cart.map(item => (
            <div key={item.id} className="cart-item">
              <img src={item.image} alt={item.name} />
              <h3>{item.name}</h3>
              <p>${item.price}</p>
              <div className="quantity-control">
                <button onClick={() => handleQuantityChange(item.id, -1)} disabled={item.quantity <= 1}>-</button>
                <span>{item.quantity}</span>
                <button onClick={() => handleQuantityChange(item.id, 1)}>+</button>
              </div>
              <button onClick={() => removeFromCart(item.id)} className="remove-btn">Remove</button>
            </div>
          ))}
        </div>
      )}

      {cart.length > 0 && (
        <div className="checkout">
          <button onClick={handleCheckout} className="checkout-btn">Proceed to Checkout</button>
        </div>
      )}
    </div>
  );
};

export default CartPage;
