import React, { useState, useEffect } from 'react';
import ProductCard from '../components/ProductCard'; // Ensure the path is correct
import '../assets/styles/Home.css';
import axios from 'axios';

const Home = ({ addToCart }) => {
  const [products, setProducts] = useState([]); // State to store fetched products
  const [error, setError] = useState(null); // To handle any potential errors
  const [searchQuery, setSearchQuery] = useState(''); // To store the search query

  // Fetch products from the backend
  useEffect(() => {
    const getProducts = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/products?search=${searchQuery}`);
        setProducts(response.data); // Store the products in state
      } catch (err) {
        setError('Failed to load products');
        console.error('Failed to load products:', err);
      }
    };
    
    getProducts();
  }, [searchQuery]);

  return (
    <div className="home">
      <h1>Welcome to Our Store</h1>
      {/* Search Input */}
      <div className="search-container">
        <input
          type="text"
          placeholder="Search products..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
      </div>
      {error && <p className="error">{error}</p>} {/* Display error message */}
      <div className="product-list">
        {products.length > 0 ? (
          products.map((product) => (
            <ProductCard key={product.id} product={product} addToCart={addToCart} />
          ))
        ) : (
          <p>Loading products...</p>
        )}
      </div>
    </div>
  );
};

export default Home;
