import React, { useState, useEffect } from 'react';
import ProductCard from '../components/ProductCard'; // Ensure the path is correct
import '../assets/styles/Home.css';
import axios from 'axios';

const Home = ({ addToCart }) => {
  const [products, setProducts] = useState([]); // State to store fetched products
  const [error, setError] = useState(null); // To handle any potential errors

  // Function to fetch products from the backend
  const getProducts = async () => {
    try {
      const response = await axios.get('http://localhost:5000/products'); // Replace with your API URL
      setProducts(response.data); // Store the products in state
    } catch (error) {
      setError('Failed to load products'); // Set error message in state
      console.error('Failed to load products:', error); // Log error
    }
  };

  // useEffect to call getProducts when the component mounts
  useEffect(() => {
    getProducts();
  }, []); // Empty array means this effect runs only once when component mounts

  return (
    <div className="home">
      <h1>Welcome to Our Store</h1>
      {error && <p>{error}</p>} {/* Display error message if any */}
      <div className="product-list">
        {products.length > 0 ? (
          products.map(product => (
            <ProductCard
              key={product.id}
              product={product}
              addToCart={addToCart} // Pass addToCart function to each ProductCard
            />
          ))
        ) : (
          <p>Loading products...</p>
        )}
      </div>
    </div>
  );
};

export default Home;
