import React from 'react';
import ProductCard from '../components/ProductCard'; // Make sure this path is correct
import '../assets/styles/Home.css'; // Assuming you have some styling for the Home component

const Home = ({ products, addToCart }) => {
  return (
    <div className="home">
      <h1>Welcome to Our Store</h1>
      <div className="product-list">
        {products.map(product => (
          <ProductCard
            key={product.id}
            product={product}
            addToCart={addToCart} // Pass the addToCart function to each ProductCard
          />
        ))}
      </div>
    </div>
  );
};

export default Home;
