import React, { useState } from 'react';
import Cart from '../components/Cart';

const CartPage = () => {
  // Sample cart data, you can replace this with a dynamic state or API call in a real-world app.
  const [cart, setCart] = useState([
    { id: 1, name: 'Product 1', price: 29.99, image: '/assets/product1.jpg', quantity: 1 },
    { id: 2, name: 'Product 2', price: 39.99, image: '/assets/product2.jpg', quantity: 2 }
  ]);

  // Function to increase the quantity of an item in the cart
  const increaseQty = (id) => {
    const updatedCart = cart.map(item => 
      item.id === id ? { ...item, quantity: item.quantity + 1 } : item
    );
    setCart(updatedCart);
  };

  // Function to decrease the quantity of an item in the cart
  const decreaseQty = (id) => {
    const updatedCart = cart.map(item => 
      item.id === id && item.quantity > 1 ? { ...item, quantity: item.quantity - 1 } : item
    );
    setCart(updatedCart);
  };

  // Function to remove an item from the cart
  const removeItem = (id) => {
    setCart(cart.filter(item => item.id !== id));
  };

  // Calculate the total price of the items in the cart
  const getTotalPrice = () => {
    return cart.reduce((total, item) => total + item.price * item.quantity, 0).toFixed(2);
  };

  return (
    <div className="container">
      <h1>Your Cart</h1>
      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <div className="cart-list">
          {cart.map(item => (
            <Cart
              key={item.id}
              item={item}
              increaseQty={increaseQty}
              decreaseQty={decreaseQty}
              removeItem={removeItem}
            />
          ))}
          <div className="cart-summary">
            <h2>Total: ${getTotalPrice()}</h2>
            <button className="checkout-btn">Proceed to Checkout</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CartPage;
