import React, { useState, useEffect } from 'react';
import ProductCard from '../components/ProductCard'; // Ensure the path is correct
import { Link } from 'react-router-dom'; // For navigation to product details
import '../assets/styles/Home.css';

const Home = ({ addToCart }) => {
  const [products, setProducts] = useState([]); // State to store fetched products
  const [searchQuery, setSearchQuery] = useState(''); // To store the search query

  // Fetch products from the backend or local data
  useEffect(() => {
    // Replace with your fetch method if you want to use actual API
    setProducts([
      { id: 1, name: 'Laptop', description: 'High-performance laptop', price: 799.99, image: 'https://via.placeholder.com/300x200?text=Laptop' },
      { id: 2, name: 'Smartphone', description: 'Latest model smartphone', price: 599.99, image: 'https://via.placeholder.com/300x200?text=Smartphone' },
      // Add other products here...
    ]);
  }, []);

  return (
    <div className="home">
      <h1>Welcome to Our Store</h1>
      {/* Search Input */}
      <div className="search-container">
        <input
          type="text"
          placeholder="Search products..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
      </div>
      <div className="product-list">
        {products.length > 0 ? (
          products.map((product) => (
            <div key={product.id} className="product-card">
              <img src={product.image} alt={product.name} className="product-image" />
              <h3>{product.name}</h3>
              <p>{product.description}</p>
              <p>${product.price}</p>
              <button onClick={() => addToCart(product)}>Add to Cart</button>
              {/* View Details Button */}
              <Link to={`/product/${product.id}`} className="view-details-btn">
                View Details
              </Link>
            </div>
          ))
        ) : (
          <p>Loading products...</p>
        )}
      </div>
    </div>
  );
};

export default Home;
