import React, { useState } from 'react';
import '../assets/styles/CheckoutPage.css';

const CheckoutPage = ({ cart, removeFromCart }) => {
  const [shippingDetails, setShippingDetails] = useState({
    name: '',
    address: '',
    city: '',
    postalCode: '',
    country: ''
  });

  const [isFormValid, setIsFormValid] = useState(true); // Track form validity

  const handleChange = (e) => {
    const { name, value } = e.target;
    setShippingDetails({
      ...shippingDetails,
      [name]: value
    });
  };

  const handleCheckout = () => {
    // Validate the form
    const { name, address, city, postalCode, country } = shippingDetails;
    if (name && address && city && postalCode && country) {
      console.log('Shipping Details:', shippingDetails);
      console.log('Cart:', cart);
      alert('Checkout successful!');
    } else {
      setIsFormValid(false); // Show error message if any field is empty
    }
  };

  const calculateTotal = () => {
    return cart.reduce((total, item) => total + item.price * item.quantity, 0).toFixed(2);
  };

  return (
    <div className="checkout-page">
      <h1>Checkout</h1>

      <div className="cart-items">
        <h2>Your Cart</h2>
        <ul>
          {cart.map((item) => (
            <li key={item.id}>
              <img src={item.image} alt={item.name} />
              <span>{item.name}</span>
              <span>Qty: {item.quantity}</span>
              <span>Price: ${item.price}</span>
              <button onClick={() => removeFromCart(item.id)}>Remove</button>
            </li>
          ))}
        </ul>
        <div className="total">
          <strong>Total: ${calculateTotal()}</strong>
        </div>
      </div>

      <div className="shipping-form">
        <h2>Shipping Information</h2>
        <form>
          <label>
            Full Name:
            <input 
              type="text" 
              name="name" 
              value={shippingDetails.name} 
              onChange={handleChange} 
              required 
            />
          </label>
          <label>
            Address:
            <input 
              type="text" 
              name="address" 
              value={shippingDetails.address} 
              onChange={handleChange} 
              required 
            />
          </label>
          <label>
            City:
            <input 
              type="text" 
              name="city" 
              value={shippingDetails.city} 
              onChange={handleChange} 
              required 
            />
          </label>
          <label>
            Postal Code:
            <input 
              type="text" 
              name="postalCode" 
              value={shippingDetails.postalCode} 
              onChange={handleChange} 
              required 
            />
          </label>
          <label>
            Country:
            <input 
              type="text" 
              name="country" 
              value={shippingDetails.country} 
              onChange={handleChange} 
              required 
            />
          </label>
        </form>

        {!isFormValid && (
          <p className="error-message">Please fill in all the fields before proceeding.</p>
        )}
      </div>

      <button onClick={handleCheckout}>Complete Checkout</button>
    </div>
  );
};

export default CheckoutPage;
