// src/App.jsx
import React, { useState } from 'react';
import ProductCard from './components/ProductCard';
import CartPage from './pages/CartPage';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';

const App = () => {
  const [cartItems, setCartItems] = useState([]);
  const [products] = useState([
    { id: 1, name: 'Product 1', description: 'Description 1', price: 29.99, imageUrl: 'https://via.placeholder.com/200' },
    { id: 2, name: 'Product 2', description: 'Description 2', price: 19.99, imageUrl: 'https://via.placeholder.com/200' },
    { id: 3, name: 'Product 3', description: 'Description 3', price: 49.99, imageUrl: 'https://via.placeholder.com/200' },
  ]);

  const handleAddToCart = (product) => {
    const existingProduct = cartItems.find(item => item.id === product.id);
    if (existingProduct) {
      setCartItems(cartItems.map(item =>
        item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
      ));
    } else {
      setCartItems([...cartItems, { ...product, quantity: 1 }]);
    }
  };

  const handleRemoveFromCart = (productToRemove) => {
    setCartItems(cartItems.filter(item => item.id !== productToRemove.id));
  };

  const handleQuantityChange = (product, delta) => {
    const updatedCartItems = cartItems.map(item =>
      item.id === product.id
        ? { ...item, quantity: Math.max(item.quantity + delta, 1) } // Prevent quantity from going below 1
        : item
    );
    setCartItems(updatedCartItems);
  };

  return (
    <Router>
      <div>
        <nav>
          <div className="logo">E-Commerce</div>
          <div>
            <a href="/">Home</a>
            <a href="/cart">Cart</a>
          </div>
        </nav>

        <Routes>
          <Route path="/" element={
            <div className="product-list">
              {products.map(product => (
                <ProductCard key={product.id} product={product} handleAddToCart={handleAddToCart} />
              ))}
            </div>
          } />
          <Route path="/cart" element={
            <CartPage
              cartItems={cartItems}
              handleRemoveFromCart={handleRemoveFromCart}
              handleQuantityChange={handleQuantityChange}
            />
          } />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
