import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Navbar from './components/Navbar';
import ProductDetails from './pages/ProductDetails';
import CartPage from './pages/CartPage';
import Footer from './components/Footer';
import Home from './pages/Home';
import './assets/styles/index.css';

const App = () => {
  const [cart, setCart] = useState(() => {
    // Load cart data from localStorage
    const savedCart = localStorage.getItem('cart');
    return savedCart ? JSON.parse(savedCart) : [];
  });
  const [searchQuery, setSearchQuery] = useState(''); // Store the search query

  const products = [
    { id: 1, name: "Laptop", description: "High-performance laptop", price: 799.99, image: "https://via.placeholder.com/300x200?text=Laptop" },
    { id: 2, name: "Smartphone", description: "Latest model smartphone", price: 599.99, image: "https://via.placeholder.com/300x200?text=Smartphone" },
    { id: 3, name: "Headphones", description: "Noise-cancelling headphones", price: 199.99, image: "https://via.placeholder.com/300x200?text=Headphones" },
    // ... (remaining products)
  ];

  // Save cart data to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cart));
  }, [cart]);

  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    product.description.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const addToCart = (product) => {
    setCart((prevCart) => {
      const existingProduct = prevCart.find(item => item.id === product.id);
      if (existingProduct) {
        return prevCart.map(item =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      } else {
        return [...prevCart, { ...product, quantity: 1 }];
      }
    });
  };

  const handleQuantityChange = (productId, change) => {
    setCart(prevCart => {
      return prevCart.map(item =>
        item.id === productId
          ? { 
              ...item, 
              quantity: Math.max(item.quantity + change, 1) 
            }
          : item
      );
    });
  };

  const removeFromCart = (productId) => {
    setCart(prevCart => prevCart.filter(item => item.id !== productId));
  };

  return (
    <Router>
      <Navbar onSearch={setSearchQuery} />
      <Routes>
        <Route
          path="/"
          element={<Home products={filteredProducts} addToCart={addToCart} />}
        />
        <Route
          path="/cart"
          element={<CartPage cart={cart} handleQuantityChange={handleQuantityChange} removeFromCart={removeFromCart} />}
        />
        <Route
          path="/product/:id"
          element={<ProductDetails products={products} addToCart={addToCart} />}
        />
      </Routes>
      <Footer />
    </Router>
  );
};

export default App;
