import React, { useEffect, useState } from 'react';
import ProductCard from '../components/ProductCard'; // Make sure this path is correct
import '../assets/styles/Home.css';
import axios from 'axios';

const Home = ({ addToCart }) => {
  const [products, setProducts] = useState([]); // Local state for products
  const [loading, setLoading] = useState(true); // Loading state
  const [error, setError] = useState(null); // Error state

  useEffect(() => {
    // Fetch products using Axios
    const fetchProducts = async () => {
      try {
        const response = await axios.get('https://api.example.com/products'); // Replace with your API endpoint
        setProducts(response.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching products:', err);
        setError('Failed to load products. Please try again later.');
        setLoading(false);
      }
    };

    fetchProducts();
  }, []); 

  if (loading) {
    return <div className="loading">Loading products...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="home">
      <h1>Welcome to Our Store</h1>
      <div className="product-list">
        {products.map(product => (
          <ProductCard
            key={product.id}
            product={product}
            addToCart={addToCart} // Pass the addToCart function to each ProductCard
          />
        ))}
      </div>
    </div>
  );
};

export default Home;
