import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; // To get the product ID from URL
import '../assets/styles/ProductDetails.css'; // Add your styling

const ProductDetails = ({ products, addToCart }) => {
  const { id } = useParams(); // Get the product ID from the URL params

  // Initialize state for selectedImage
  const [selectedImage, setSelectedImage] = useState(null); // Default to null
  const [product, setProduct] = useState(null); // State to store product details

  // Fetch the product by ID when component mounts or product changes
  useEffect(() => {
    const product = products.find((product) => product.id === parseInt(id)); 
    if (product) {
      setProduct(product); // Set the product details if found
      if (product.images && product.images.length > 0) {
        setSelectedImage(product.images[0]); // Set first image as default
      }
    }
  }, [id, products]); // Re-run the effect if id or products change

  // If product is not found, return early
  if (!product) {
    return <div>Product not found!</div>;
  }

  return (
    <div className="product-details">
      <div className="product-details-container">
        {/* Left side: Image Gallery */}
        <div className="product-images">
          <div className="main-image">
            {selectedImage && <img src={selectedImage} alt={product.name} className="main-product-image" />}
          </div>
          <div className="image-thumbnails">
            {product.images && product.images.length > 0 ? (
              product.images.map((image, index) => (
                <img
                  key={index}
                  src={image}
                  alt={`${product.name} thumbnail`}
                  className="thumbnail"
                  onClick={() => setSelectedImage(image)}
                />
              ))
            ) : (
              <p>No images available</p>
            )}
          </div>
        </div>

        {/* Right side: Product Info */}
        <div className="product-info">
          <h2 className="product-title">{product.name}</h2>
          <p className="product-price">${product.price}</p>
          <p className="product-description">{product.description}</p>
          
          {/* Quick Add to Cart Button */}
          <button className="add-to-cart-btn" onClick={() => addToCart(product)}>
            Add to Cart
          </button>
          
          {/* Product Specifications */}
          <div className="product-specifications">
            <h3>Specifications</h3>
            <ul>
              <li>Brand: {product.brand}</li>
              <li>Category: {product.category}</li>
              <li>Weight: {product.weight} kg</li>
              <li>Dimensions: {product.dimensions}</li>
            </ul>
          </div>

          {/* Product Ratings and Reviews */}
          <div className="product-reviews">
            <h3>Customer Reviews</h3>
            <div className="rating">
              <span className="stars">
                {'★'.repeat(product.rating)}
                {'☆'.repeat(5 - product.rating)}
              </span>
              <span className="rating-count">({product.reviews ? product.reviews.length : 0} reviews)</span>
            </div>
            <div className="reviews-list">
              {product.reviews && product.reviews.length > 0 ? (
                product.reviews.map((review, index) => (
                  <div key={index} className="review">
                    <p className="review-user">{review.user}</p>
                    <p className="review-comment">{review.comment}</p>
                  </div>
                ))
              ) : (
                <p>No reviews available</p>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
