import React from 'react';
import { useParams } from 'react-router-dom'; // Import useParams hook to get the product ID from the URL
import '../assets/styles/ProductDetails.css'; // Assuming you have styles for ProductDetails

const ProductDetails = ({ products }) => {
  const { id } = useParams(); // Extract the product ID from the URL
  const product = products.find((product) => product.id === parseInt(id)); // Find the product by ID

  if (!product) {
    return <div>Product not found!</div>; // Display message if product is not found
  }

  return (
    <div className="product-details">
      <img src={product.image} alt={product.name} className="product-details-image" />
      <div className="product-details-info">
        <h2>{product.name}</h2>
        <p>{product.description}</p>
        <p className="product-details-price">${product.price}</p>
        <button className="add-to-cart-btn">Add to Cart</button>
      </div>
    </div>
  );
};

export default ProductDetails;
